class Token {

    constructor(tokenInstance) {
        this.props = tokenInstance;
    }

    render() {
        let message = `Given approval for ${this.props.symbol}`;
        var amountFixed = (this.props.value / 10**this.props.decimals).toFixed(2);
        let subText = `You have approved ${this.props.spender} to move ${amountFixed} ${this.props.symbol}`;
        return`
        <div class="ui container">
          <div class="ui segment">
            <img src="data:image/png;base64,">
            <span><bold><h3>${message}</h3></bold></span>
            <p>${subText}</p>
          </div>
        </div>
`;
    }
}

web3.tokens.dataChanged = (oldTokens, updatedTokens, tokenIdCard) => {
    const currentTokenInstance = web3.tokens.data.currentInstance;
    document.getElementById(tokenIdCard).innerHTML = new Token(currentTokenInstance).render();
};
