                class Token {
    constructor(tokenInstance) {
        this.props = tokenInstance
    }

    render() {
        var balanceFixed = (this.props.balance / 10**this.props.decimals).toFixed(2);
        return `
        <div class="ui container">
              <div class="ui segment">
                <h3>Approve account to use your ${this.props.name}</h3>
                <img src="data:image/png;base64,
                "/>
                <br>
                <br>
                <span>${this.props.symbol} Balance: <b>${balanceFixed}</b><br></span>
                <input type="text" id="approval" placeholder="Approval Address"/>
                <input type="number" id="amount" placeholder="Amount to approve"/>
              </div>
        </div>`;
    }
}

web3.tokens.dataChanged = (oldTokens, updatedTokens, tokenCardId) => {
    const currentTokenInstance = updatedTokens.currentInstance;
    document.getElementById(tokenCardId).innerHTML = new Token(currentTokenInstance).render();
};
